#                           Airy functions
#
#                       airyai(x), airybi(x)
#                       airyaix(x), airybix(x)
#                   airyaiprime(x), airybiprime(x)
#                   airyaiprimex(x), airybiprimex(x)
#
#    Routines for computing Airy functions, their derivatives, and scaled versions for real arguments.
#    These routines are loosely based on the methods reported in [1] using their asymptotic expansions.
#    Asymptotic coefficiens have been explicitly calculated and unrolled to desired amount of coefficients.
#    For negative arguments, Euler's equation was used to avoid complex arithmetic using the forms given in [1].
#    Where asymptotic expansions are not valid (≈|x|<10), minimax rational and polynomial approximations, taylor series
#    and Chebyshev series are used. The airy functions are oscillatory for negative arguments and exponential for positive arguments.
#    Taylor series are used around the zeros of the Airy function's for negative arguments to preserve accuracy.
#    These were generated in Mathematica using the form `InputForm[Series[AiryAi[x], {x, 2.0, 33}]]`.
#    The scaled versions were also calculated with `InputForm[Series[AiryBiPrime[x] * Exp[-2 x^(3/2) / 3] * x^(1/4), {x, 7.000000000000000000000, 42}]]`.
#    Higher precisions coefficients were generated then rounded to Float64. The one caveat is the generation of the Taylor series
#    around function zeros. The accuracy of the routine was compared to Arb accessed through ArbNumerics.jl.
#    The roots of the Airy function were calculated in high precision then rounded to Float64 and then converted to an Arb numbers
#    with ArbNumerics.ArbFloat(root) which was the value used for the Taylor series in high precision.
#    The routine also uses rational function approximations from the Cephes library [2] for the airyai and airyaiprime routines.
#    There was a small bug reported with these approximations [3] that caused SciPy to switch back to the amos expansions.
#    This bug was investigated but couldn't reproduce, however, the default precision in the Arb library must be increased to give correct results.
#    The switch from the rational approximation back to the asymptotic expansions for very large arguments (x>1e3) was for better handling of infinite
#    and very large arguments while being faster by avoiding the division.
#
#
# [1] Jentschura, Ulrich David, and E. Lötstedt. "Numerical calculation of Bessel, Hankel and Airy functions." 
#     Computer Physics Communications 183.3 (2012): 506-519.
# [2] Cephes Math Library Release 2.8:  June, 2000
#     Copyright 1984, 1987, 1989, 2000 by Stephen L. Moshier
# [3] https://github.com/scipy/scipy/pull/502

#
# Generate function definitions for separate positive and negative argument branches
# Large negative arguments return domain error due to loss of precision
# Scaled versions of airyai throw domain error for negative arguments as function is complex
#

for f in (:airyai, :airyaiprime, :airybi, :airybiprime, :airybix, :airybiprimex)
    f_pos_args = Symbol(f, :_pos_args)
    f_neg_args = Symbol(f, :_neg_args)
    @eval function $f(x::Float64)
        if x >= 0.0
            return $f_pos_args(x)
        else
            if x >= -1e8
                return $f_neg_args(x)
            else
                isnan(x) && return x
                throw(DomainError(x, "Loss of significant digits for large negative arguments. Requires a higher precision routine."))
            end
        end
    end
end

for f in (:airyaix, :airyaiprimex)
    f_pos_args = Symbol(f, :_pos_args)
    @eval function $f(x::Float64)
        if x >= 0.0
            return $f_pos_args(x)
        else
            isnan(x) && return x
            throw(DomainError(x, "Complex result returned for real arguments. Use complex argument: $f(complex(x))"))
        end
    end
end

#
# Positive argument branch for airyai
# Routines compute both airyai and airyaiprime
#

@inline function airyai_aiprime_pos_args(x)
    if x >= 2.06
        isinf(x) && return (0.0, 0.0)

        c = exp(-x * sqrt(x) / 3)
        aix, aiprimex = airyaix_large_pos_args(x)
        ai = (c * aix) * c
        aiprime = (c * aiprimex) * c
    else
        ai, aiprime = _airyai_small_pos_args(x)
    end
    return ai, aiprime
end

@inline function airyaix_aiprimex_pos_args(x)
    if x >= 2.06
        aix, aiprimex = airyaix_large_pos_args(x)
    else
        # multiply by scale factor
        c = exp(2 * x * sqrt(x) / 3)
        ai, aiprime = _airyai_small_pos_args(x)

        aix = c * ai
        aiprimex = c * aiprime
    end
    return aix, aiprimex
end

@inline function _airyai_small_pos_args(x)
    if x < 0.1
        # minimax polynomial
        ai = evalpoly(x, (0.3550280538878172, -0.2588194037928022, -9.261034519371688e-13, 0.0591713423857866, -0.021568286307597485, 5.279319501583404e-8, 0.001971826010638854, -0.0005109862484427906))
        aiprime = evalpoly(x, (-0.2588194037928068, 7.026072286612935e-16, 0.1775140269437213, -0.08627313457837005, -9.890864876107356e-10, 0.011834297039414418, -0.0035951989961820843, 4.799599033246355e-6, 0.0002209248351055058))
    else
        # rational approximations for airyai, airyaiprime 0.0 < x < 2.1
        # airyai
        p1 = evalpoly(x, (0.35502805388781744, -0.10482341989661095, -0.0486577708334799, 0.027567409888166392, -0.003289115420423328, -0.0007305591670017397, 0.00028980078696335705, -4.0935293322551856e-5, 2.843352302971319e-6, -8.16006902427902e-8)) 
        q1 = evalpoly(x, (1.0, 0.43375722625250424, 0.1791605343840418, 0.04159189705098047, 0.009514630923507036, 0.0013695863161550792, 0.00021134924004087647, 1.643834172337603e-5, 1.820647551730948e-6))

        # airyaiprime
        p2 = evalpoly(x, (-0.25881940379280843, -0.1281771862490461, 0.1215372422831532, -0.012741106612258057, -0.007817713517073376, 0.0024841727869283543, -0.00022800908016912754, -1.0310082365339424e-5, 3.234478554177193e-6, -1.6817588355348419e-7)) 
        q2 = evalpoly(x, (1.0, 0.4952379318195294, 0.2162773881682495, 0.05555863041440847, 0.013462108119986573, 0.0021389588233556925, 0.0003499286802943533, 3.0276702049000568e-5, 3.5914680052925263e-6))

        ai = p1 / q1
        aiprime = p2 / q2
    end
    return ai, aiprime
end

# asymptotic expansions for airyai
# positive arguments are asymptotically scaled
# negative arguments are not

@inline function airyaix_large_pos_args(x::T) where T <: Float64
    if x > 1000.0
        # asymptotic expansion
        invx3 = 1 / (x * x * x)
        p = evalpoly(invx3, (1.5707963267948966, 0.13124057851910487, 0.4584353787485384))
        q = evalpoly(invx3, (0.1636246173744684, 0.20141783231057064, 1.3848568733028765))
        p1 = evalpoly(invx3, (-1.5707963267948966, 0.15510250188621483, 0.4982993247266722))
        q1 = evalpoly(invx3, (0.22907446432425577, 0.22511404787652015, 1.4803642438754887))

        xsqr = sqrt(x)
        xsqrx = 1 / (x * xsqr)
        a = muladd(xsqrx, -q, p)
        c = muladd(xsqrx, -q1, p1)
        xsqr = sqrt(xsqr)
        ai = a / (PIPOW3O2(T) * xsqr)
        aip = c * xsqr * inv(PIPOW3O2(T))
        return ai, aip
    else
        # rational approximation from cephes
        xsqr = sqrt(x)
        zinv = 3 / (2 * x * xsqr)
        xsqr = sqrt(xsqr)
        p = evalpoly(zinv, (9.99999999999999995305e-1, 1.40264691163389668864e1, 7.05360906840444183113e1, 1.59756391350164413639e2, 1.68089224934630576269e2, 7.62796053615234516538e1, 1.20075952739645805542e1, 3.46538101525629032477e-1))
        q = evalpoly(zinv, (1.00000000000000000470e0, 1.40959135607834029598e1, 7.14778400825575695274e1, 1.64234692871529701831e2, 1.77318088145400459522e2, 8.45138970141474626562e1, 1.47562562584847203173e1, 5.67594532638770212846e-1))
        p1 = evalpoly(zinv, (1.00000000000000000550e0, 1.39470856980481566958e1, 6.99778599330103016170e1, 1.59317847137141783523e2, 1.71184781360976385540e2, 8.20584123476060982430e1, 1.47454670787755323881e1, 6.13759184814035759225e-1))
        q1 = evalpoly(zinv, (9.99999999999999994502e-1, 1.38498634758259442477e1, 6.86752304592780337944e1, 1.53206427475809220834e2, 1.58778084372838313640e2, 7.11727352147859965283e1, 1.11810297306158156705e1, 3.34203677749736953049e-1))
        ai = (ONEOSQPI(T)/2) * p / (q * xsqr)
        aip = -(ONEOSQPI(T)/2) * xsqr * p1 / q1
        return ai, aip
    end
end

