function Uk_poly_Kn(p, v, p2, ::Type{Float32})
    u0 = one(p)
    u1 = 1 / 24 * evalpoly(p2, (3, -5))
    u2 = 1 / 1152 * evalpoly(p2, (81, -462, 385))
    return evalpoly(-p/v, (u0, u1, u2))
end
